class Rhodonea{constructor(){this.key="rhodonea",this.name="Rhodonea (Rose) Curve";let max_r=Math.min(max_x-min_x,max_y-min_y)/2;this.config={amplitude:{name:"Amplitude",value:null,input:{type:"createSlider",params:[0,max_r,max_r,1],class:"slider",displayValue:!0}},petals:{name:"Petal Value (k)",value:null,input:{type:"createSlider",params:[.5,20,5,.5],class:"slider",displayValue:!0}}},this.path=[]}draw(){this.config.amplitude.value=parseFloat(document.querySelector("#pattern-controls > div:nth-child(1) > input").value),this.config.petals.value=parseFloat(document.querySelector("#pattern-controls > div:nth-child(2) > input").value),document.querySelector("#pattern-controls > div.pattern-control:nth-child(1) > span").innerHTML=this.config.amplitude.value,document.querySelector("#pattern-controls > div.pattern-control:nth-child(2) > span").innerHTML=this.config.petals.value;let path=this.calc(this.config.amplitude.value,this.config.petals.value);return this.path=path,path}calc(amplitude,petals){var x,y,theta=0,path=new Array,step=0;let period=2*Math.PI,steps_per_revolution=500;for(;theta<period;)theta=step/500*period,x=amplitude*Math.sin(petals*theta)*Math.cos(theta),y=amplitude*Math.sin(petals*theta)*Math.sin(theta),path.push([x,y]),step++;return path}}